mapper.update:
UPDATE TBL_BOARD
SET BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{boardContent}, BOARD_UPDATE_DATE = SYSDATE
WHERE BOARD_NUMBER = #{boardNumber}

mapper.select:
SELECT BOARD_NUMBER, BOARD_DATE, BOARD_UPDATE_DATE, BOARD_TITLE, BOARD_CONTENT, BOARD_REWARD, BOARD_CATEGORY
FROM TBL_BOARD
WHERE BOARD_NUMBER = #{boardNumber}
