mapper.update:
UPDATE TBL_BOARD SET
BOARD_UPDATE_DATE = SYSDATE, BOARD_TITLE = #{boardTitle}, BOARD_CONTENT = #{boardContent},
BOARD_REWARD = #{boardReward}, BOARD_CATEGORY = #{boardCategory}, BOARD_ADOPT = #{boardAdopt},
BOARD_REPORT = #{boardReport}, MEMBER_NUMBER = #{memberNumber}
WHERE BOARD_NUMBER = #{boardNumber}
