mapper.update:
UPDATE TBL_MEMBER SET
MEMBER_ID = #{memberId}, MEMBER_PW = #{memberPw}, MEMBER_EMAIL = #{memberEmail},
MEMBER_NAME = #{memberName}, MEMBER_NICKNAME = #{memberNickname}, MEMBER_MOBILE = #{memberMobile},
MEMBER_RANK = #{memberRank}
WHERE MEMBER_NUMBER = #{memberNumber}

mapper.select:
SELECT MEMBER_NUMBER, MEMBER_NAME, MEMBER_ID, MEMBER_PW, MEMBER_EMAIL, MEMBER_NICKNAME, MEMBER_MOBILE, MEMBER_RANK, MEMBER_STATUS
FROM (SELECT * FROM TBL_MEMBER ORDER BY MEMBER_NUMBER DESC)
WHERE MEMBER_STATUS = 1 AND ROWNUM <= #{criteria}
